#!/bin/bash

type arm-linux-gnueabihf-gcc >/dev/null 2>&1 || { 
	echo >&2 "I require ARM gcc.  Exporting PATH."; 
	export PATH=$HOME/bps/crosscomp/tools/arm-bcm2708/arm-rpi-4.9.3-linux-gnueabihf/bin:$PATH 
}
CC=arm-linux-gnueabihf-gcc CXX=arm-linux-gnueabihf-g++ node-gyp clean configure --arch=arm rebuild