#!/bin/bash

echo "Sync with probe ..."
echo "~~~~~~~~~~~~~~~~~~~"
pwd
read -p "Do you wish to sync with probe at IP: $1 now? (y/N) " decision
if [ "$decision" == "y" ]; then
 echo "Ok, now deploying...(enter server pw when asked for)"
 rsync -rav --exclude='.*' --exclude='*.ts' --exclude='node_modules'  \
 . pi@$1:/home/pi/node-tuner/
fi
